using namespace std;

template<class T>
struct BIT {
	struct Node {
		T sum0 = 0;
		T sum1 = 0;
	};

	vector<Node> nodes;

	BIT(int n) : nodes(n + 1) {}

	T sumRange(int k) {
		k++;
		T s0 = 0;
		T s1 = 0;
		int x = k;
		while (k > 0) {
			s0 += nodes[k].sum0;
			s1 += nodes[k].sum1;
			k -= k & -k;
		}
		return s0 + s1 * x;
	}

	T sumRange(int l, int r) {
		return sumRange(r - 1) - sumRange(l - 1);
	}

	T sumPoint(int k) {
		k++;
		T ret = 0;
		while (k > 0) {
			ret += nodes[k].sum1;
			k -= k & -k;
		}
		return ret;
	}

	void addPoint(int k, T v) {
		k++;
		while (k < nodes.size()) {
			nodes[k].sum0 += v;
			k += k & -k;
		}
	}

	void addRange(int l, int r, T value) {
		if (l == r) {
			return;
		}
		add(l, value);
		add(r, -value);
	}

	void add(int k, T v1) {
		T v0 = v1 * k;
		k++;
		while (k < nodes.size()) {
			nodes[k].sum0 -= v0;
			nodes[k].sum1 += v1;
			k += k & -k;
		}
	}
};

int main() {
	int n, q;
	cin >> n >> q;

	const int S = 500;
	const int N = (n + S - 1) / S;

	vector<vector<int>> g(n);
	vector<long long> a(n);
	vector<long long> aa(n);
	for (int i = 0; i < n; i++) {
		int p;
		scanf("%d %lld", &p, &aa[i]);
		if (p != -1) {
			g[p].push_back(i);
		}
	}
	vector<int> type(q);
	vector<int> foo(q), bar(q), baz(q);
	bool has3 = false;
	for (int i = 0; i < q; i++) {
		scanf("%d", &type[i]);
		if (type[i] == 1) {
			scanf("%d %d %d", &foo[i], &bar[i], &baz[i]);
		} else if (type[i] == 2) {
			scanf("%d %d", &foo[i], &bar[i]);
		} else if (type[i] == 3) {
			scanf("%d %d", &foo[i], &bar[i]);
			has3 = true;
		}
	}

	if (!has3) {
		vector<vector<int>> dict(N);
		vector<BIT<long long>> bits;
		vector<int> L(n);
		vector<int> R(n);
		vector<int> depth(n);
		vector<int> depth2(n);
		int now = 0;
		function<void(int, int)> dfs = [&](int u, int d) {
			L[u] = now;
			a[now] = aa[u];
			depth[now] = d;
			now++;
			for (int v : g[u]) {
				dfs(v, d + 1);
			}
			R[u] = now;
		};
		dfs(0, 0);

		auto lb = [&](vector<int> &v, int val) {
			return lower_bound(v.begin(), v.end(), val) - v.begin();
		};

		for (int i = 0; i < n; i++) {
			dict[i / S].push_back(depth[i]);
		}
		for (int i = 0; i < N; i++) {
			sort(dict[i].begin(), dict[i].end());
			bits.emplace_back(dict[i].size());
		}
		for (int i = 0; i < n; i++) {
			depth2[i] = lb(dict[i / S], depth[i]);
			bits[i / S].addPoint(depth2[i], a[i]);
		}

		auto add = [&](int u, int k, int x) {
			int l = L[u];
			int r = R[u];
			int bottom = depth[l];
			int top = depth[l] + k + 1;
			while (l < r) {
				if (l % S == 0 && l + S <= r) {
					int b = lb(dict[l / S], bottom);
					int t = lb(dict[l / S], top);
					bits[l / S].addRange(b, t, x);
					l += S;
				} else {
					if (bottom <= depth[l] && depth[l] < top) {
						a[l] += x;
						bits[l / S].addPoint(depth2[l], x);
					}
					l++;
				}
			}
		};

		auto sum = [&](int u, int k) {
			int l = L[u];
			int r = R[u];
