vector<int> seg[MAX * 4];
sum_seg S[MAX * 4];

inline void add(int b, int l, int r, int ll, int x){
	if (l <= ll&&ll < r){
		seg[b].push_back(x);
		if (l + 1 < r){
			add(b * 2 + 1, l, (l + r) >> 1, ll, x);
			add(b * 2 + 2, (l + r) >> 1, r, ll, x);
		}
	}
}
inline void init(int b, int l, int r){
	int siz = seg[b].size();
	sort(seg[b].begin(), seg[b].end());
	S[b].resize(siz);
	if (l + 1 < r){
		init(b * 2 + 1, l, (l + r) >> 1);
		init(b * 2 + 2, (l + r) >> 1, r);
	}
}
inline void add1(int b, int l, int r, int ll, int rr, int lll,int rrr,int x){
	if (r <= ll || rr <= l){
		return;
	}
	if (ll <= l&&r <= rr){
		lll = upper_bound(seg[b].begin(), seg[b].end(), lll - 1) - seg[b].begin();
		rrr = upper_bound(seg[b].begin(), seg[b].end(), rrr) - seg[b].begin();
		if (lll >= rrr)return;
		S[b].add(0, 0, seg[b].size(), lll, rrr, x);
		return;
	}
	add1(b * 2 + 1, l, (l + r) >> 1, ll, rr, lll, rrr, x);
	add1(b * 2 + 2, (l + r) >> 1, r, ll, rr, lll, rrr, x);
}
inline long long int Q1(int b, int l, int r, int ll, int rr, int lll, int rrr){
	if (r <= ll || rr <= l)return 0;
	if (ll <= l&&r <= rr){
		lll = upper_bound(seg[b].begin(), seg[b].end(),lll-1) - seg[b].begin();
		rrr = upper_bound(seg[b].begin(), seg[b].end(), rrr) - seg[b].begin();
		if (lll >= rrr)return 0;
		return S[b].query(0, 0, seg[b].size(), lll, rrr);
	}
	return Q1(b * 2 + 1, l, (l + r) >> 1, ll, rr, lll, rrr) + Q1(b * 2 + 2, (l + r) >> 1, r, ll, rr, lll, rrr);
}

