const int MAX = 1<<19;
ll dat[2*MAX];
ll lazy[2*MAX];
void push(int k){
  if(lazy[k]!=0){
    dat[k] += lazy[k];
    if(k<MAX-1){
      lazy[2*k+1] += lazy[k];
      lazy[2*k+2] += lazy[k];
    }
    lazy[k] = 0;
  }
}
void add(int l,int r,int v,int a,int b,int k){
  push(k);
  if(r<=a || b<=l)return;
  if(l<=a && b<=r){
    lazy[k] += v;
  }else{
    int m = (a+b)/2;
    add(l,r,v,a,m,2*k+1);
    add(l,r,v,m,b,2*k+2);
  }
}
ll sum(int l,int r,int a,int b,int k){
  push(k);
  if(r<=a || b<=l)return 0ll;
  if(l<=a && b<=r){
    return dat[k];
  }else{
    int m = (a+b)/2;
    return sum(l,r,a,m,2*k+1) + sum(l,r,m,b,2*k+2);
  }
}

