class BIT //[1, n]
{
    int n;
    long[] bit;

    public BIT(int N)
    {
        n = N;
        bit = new long[n + 1];
    }

    public void Add(int i, long x)
    {
        while (i <= n)
        {
            bit[i] += x;
            i += i & -i;
        }
    }

    // [1, i] sum
    public long Sum(int i)
    {
        long s = 0;
        while (i > 0)
        {
            s = s + bit[i];
            i -= i & -i;
        }
        return s;
    }

    //[a, b] sum
    public long Sum(int a, int b)
    {
        return Sum(b) - Sum(a - 1);
    }
}
