fn solve_linear(q: usize, mut a: Vec<i64>) {
    for _ in 0 .. q {
        let kind: usize = get();
        match kind {
            1 => {
                let v: usize = get();
                let d: usize = get();
                let x = get();
                if v + d < a.len() {
                    a[v + d] += x;
                }
                a[v] += x;
            },
            2 => {
                let v: usize = get();
                let d: usize = get();
                let mut tot = a[v];
                if v + d < a.len() {
                    tot += a[v + d];
                }
                println!("{}", tot);
            },
            3 => {
                let pr: i32 = get();
                let ar = get();
                assert!(pr + 1 == a.len() as i32);
                a.push(ar);
            },
            _ => panic!(),
        }
    }
}

fn solve() {
    let n = get();
    let q = get();
    let mut par = vec![0; n];
    let mut a = vec![0; n];
    for i in 0 .. n {
        par[i] = get();
        a[i] = get();
    }
    let mut is_linear = true;
    for i in 0 .. n {
        if par[i] != i as i32 - 1 {
            is_linear = false;
        }
    }
    if is_linear {
        solve_linear(q, a);
        return;
    }
}

