object Main extends App {
  val sc = new Scanner(System.in)
  val n = sc.nextInt
  val a = Array.fill(n)(sc.nextInt)


  run()

  def run(): Unit = {
    if(firstWin()) println("FirsSt")
    else println("Second")
  }

  def firstWin(): Boolean = {
    val nEven = a.count(i => i > 1 && i % 2 == 0)
    val nOdd = a.count(i => i > 1 && i % 2 == 1)
    if(nEven % 2 == 1) true
    else {
      if(nOdd > 1 || nOdd == 0) false
      else {
        val id = a.zipWithIndex.filter(x => x._1 > 1 && x._1 % 2 == 1).apply(0)._2
        a(id) -= 1
        val m = a.reduceLeft((g, i) => gcd(g, i))
        for(i <- 0 until a.size) a(i) /= m
        !firstWin()
      }
    }
  }

  // a <= b
  def gcd(a: Int, b: Int): Int = {
    val c = b % a
    if(c == 0) a
    else gcd(c, a)
  }
}

