int main()
{
    int N;
    std::cin >> N;
    std::vector<ll> A(N);
    std::cin >> A;
    auto ok = [&, N](auto&& self, std::vector<ll>& A) {
        if (A == std::vector<ll>(N, 1LL)) { return false; }
        int even = 0;
        for (int i = 0; i < N; i++) { even += (A[i] % 2 == 0 ? 1 : 0); }
        const int odd = N - even;
        if (even % 2 == 1) {
            return true;
        } else if (odd >= 2) {
            return false;
        } else {
            ll g = 0;
            for (int i = 0; i < N; i++) {
                if (A[i] % 2 == 1) { A[i]--; }
                g = gcd(g, A[i]);
            }
            for (int i = 0; i < N; i++) { A[i] /= g; }
            return not self(self, A);
        }
    };
    std::cout << (ok(ok, A) ? "First" : "Second") << std::endl;
    return 0;
}

