using namespace std;

int Cint[7] = { 4, 6, 8, 10, 100, 1000, 10000 };
bool end = false;
bool y[7];	//4,6,8,10,100,1000,100000
//0,1,2,3, 4,   5,   6
bool now = true; // chokudai_s
int ch(int n) {
	for (int i = 6; i >= 0; i--) {
		if (n == 1) {
			end = true;
			return -1;
		}
		end = true;
		if (y[i] && n % Cint[i] != 0) {
			y[i] = false;
		}
	}
	return 0;

}
void change(bool *n, int cnt) {
	if (cnt % 2 != 0) {
		if (n)
			*n = false;
		else
			*n = true;
	}
	return;
}
void re(int n, int *ar) {
	int cn = 0, tmp;
	for (int i = 6; i >= 0; i--) {
		if (y[i]) {
			fill(y[0], y[6], true);
			for (int j = 0; j < n; j++) {
				ar[j] = ar[j] / Cint[i];
				if (ar[j] % 2 == 0) {
					ch(ar[j]);
				} else {
					cn++;
					tmp = ch(ar[j] - 1);
					if (tmp != -1) {
						ar[j]--;
					}
				}
			}
			change(&now, cn);
			return;
		}
	}
	fill(y[0], y[6], true);
	for (int j = 0; j < n; j++) {
		ar[j] = ar[j] / 2;
		if (ar[j] % 2 == 0) {
			ch(ar[j]);
		} else {
			cn++;
			tmp = ch(ar[j] - 1);
			if (tmp != -1) {
				ar[j]--;
			}
		}
	}
	change(&now, cn);
	return;

}

