	ios::sync_with_stdio(false);
	fill(y[0], y[6], true);	//clear
	int n;
	cin >> n;
	int ar[n];
	list<int> p[n];
	list<int> l;
	stack<int> ki, gu;

	int tmpCn = 0;
	for (int i = 0; i < n; ++i) {
		cin >> ar[i];
		if (ar[i] % 2 == 0) {
			ch(ar[i]);
		}

		else {
			tmpCn++;
			ch(ar[i] - 1);
			ar[i]--;
		}
	}
	change(&now, tmpCn);
	re(n, ar);
	while (true) {
		re(n, ar);
		if (end) {
			if (now)
				cout << "Second" << endl;
