      if(a[i]%2==1) {
        if(idx!=-1||a[i]==1){
          flag = false;
          break;
        }
        idx = i;
      }
    }
    if(!flag) break;
    a[idx]--;
    ll g = a[0];

    for(int i=1;i<n;i++) g = gcd(g, a[i]);
    ll p = 0;
    for(int i=0;i<n;i++) p += (a[i] - 1);
    ll t = 0;
    for(int i=0;i<n;i++){
      a[i]/=g;
      t += a[i] - 1;
    }
    if(p%2==t%2) break;
    tm++;
  }
  //std::cout << tm << '\n';
  std::cout << (tm%2?"First":"Second") << '\n';
  return 0;
}

