void solve() {
  int n; cin >> n;
  vi arr(n); cin >> arr;

  assert(n > 1 || arr[0] == 1);

  if (!(n & 1)) {
    CoutYN(accumulate(ALL(arr), 0LL) & 1, "First", "Second");
    return;
  }

  sort(ALL(arr));

  bool turn = false;

  while (arr[0] > 1) {
    int idx;
    int cnt = 0;

    rep(i,n) {
      if (arr[i] & 1) {
        idx = i;
        ++cnt;
      }
    }

    if (cnt > 1) {
      CoutYN((cnt & 1) ^ turn, "Second", "First");
      return;
    }

    --arr[idx];

    int d = arr[0];
    rep1(i,n) d = GCD(d, arr[i]);
    rep(i,n) arr[i] /= d;

    turn ^= true;
  }

  CoutYN((accumulate(ALL(arr), 0LL) & 1) ^ turn, "Second", "First");
}


