bool solve() {
  bool flag = false;
  LL sum = 0, cnt = 0;
  for(int i = 0; i < n; i++) {
    if(arr[i] == 1) {
      flag = true;
    }
    cnt += arr[i] % 2;
    sum += arr[i] - 1;
  }
  if(flag) {
    return sum % 2 == 1;
  }
  if(cnt == 1 and n % 2 == 1) {
    for(int i = 0; i < n; i++) {
      arr[i] /= 2;
    }
    return !solve();
  }
  if((cnt % 2) != (n % 2)) return true;
  return false;
}

