#include <iostream>
#include <algorithm>
#include <numeric>
using LL = long long ;

const int kN = 100000 + 5;
int n, a[kN];

int gcd(int a, int b)
{
    return b == 0 ? a : gcd(b, a % b);
}

bool work()
{
    if (n == 1) return a[0] != 1;
    int odd = std::count_if(a, a + n, [&](int x){return x % 2 == 1;});
    if (*std::min_element(a, a + n) == 1) {
        return std::accumulate(a, a + n, 0ll) - n & 1;
    } else if (n > 2) {
        if (odd >= 2) {
            return std::accumulate(a, a + n, 0ll) - n & 1;
        } else {
            for (int i = 0; i < n; ++ i) if (a[i] & 1) {
                if (a[i] == 1) return false;
                a[i] --;
            }
            int x = 0;
            for (int i = 0; i < n; ++ i)
                x = gcd(x, a[i]);
            for (int i = 0; i < n; ++ i)
                a[i] /= x;
            return !work();
        }
    } else {
        if (a[0] % 2 == 1 && a[1] % 2 == 1) return false;
        else return true;
    }
}

int main()
{
    std::cin >> n;
    for (int i = 0; i < n; ++ i) std::cin >> a[i];
    std::cout << (work() ? "First" : "Second") << std::endl;
}

