        if (n % 2 != nOdd % 2)
        {
            // first wins, no need to do shit

            cout << (!swap ? "First" : "Second") << '\n';
            return 0;
        }
        else
        {
            // try swap
            if (nOdd == 1)
            {
                //int g = 0;
                int mpower = 32;
                for (int i = 0; i < n; i++)
                {
                    if (a[i] % 2 == 1)
                    {
                        a[i]--;
                    }
                    //g = __gcd(g, a[i]);
                    int p = 0;
                    while (p < 32 && (a[i] & ((1 << p) - 1)) == 0)
                    {
                        p++;
                    }
                    p--;
                    //printf("power %d\n", p);
                    mpower = min(mpower, p);
                }

                for (int i = 0; i < n; i++)
                    a[i] >>= mpower;

                swap = !swap;
            }
            else
            {
                cout << (!swap ? "Second" : "First") << '\n';
                return 0;
            }
        }
    }

    return 0;
}

