bool solve(vector<int> a) {
	int n = a.size();
	if (n % 2 == 0) {
		int res = 0;
		for (int x : a) {
			res = (res ^ x) & 1;
		}
		return res;
	}

	int s = 0;
	for (int x : a) {
		s += x % 2;
	}
	if (s > 1 || *min_element(all(a)) == 1) {
		return (s % 2 != n % 2);
	} else {
		if (s % 2 != n % 2) {
			return 1;
		}
		int g = 0;
		for (int& x : a) {
			x -= x % 2;
			g = __gcd(g, x);
		}
		for (int& x : a) {
			x /= g;
		}
		return !solve(a);
	}
}

