				else gcd = __gcd(gcd, a[i] - 1);
			}
			else {
				gcd = __gcd(gcd, a[i]);
				even ++;
			}
		}
		if(even % 2) puts(K % 2 ? "First" : "Second");
		else {
			if(odd == 1 && f) {
				for(int i = 1; i <= n; i ++) a[i] /= gcd;
			}
			else puts(K % 2 ? "Second" : "First");
		}
	}
}

