bool solve(vector<int>& a) {
	int n = a.size();
	bool eq = false;
	ll sum = 0;
	F(i, n) {
		if (a[i] == 1)eq = true;
		sum += a[i];
	}
	if (eq) {
		if (sum % 2 == n % 2) {
			return false;
		}
		else {
			return true;
		}
	}
	int cnt = 0;
	F(i, n) {
		if (a[i] % 2 == 0) cnt++;
	}
	if (cnt & 1) {
		return true;
	}
	if (cnt != n - 1) {
		return false;
	}
	F(i, n)a[i] /= 2;
	return !solve(a);
}

