int gcd( int m, int n )
{
	int a=max(m,n),b=min(m,n);
	if(m==0) return n;if(n==0) return m;
	if(a%b==0) return b;
	return gcd(b,a-b*(a/b));
}
int cal(vector<int> a){
	int n=a.size(),odd=0,even=0,oddn,g=0;
	rep(i,n){
		if(a[i]%2==0) even++;
		else odd++,oddn=i;
	}
	assert(odd>0);
	if(n==1) return even;
	if(even%2==1) return 0;
	if(odd>1) return 1;
	a[oddn]--;
	rep(i,n) g=gcd(g,a[i]);
	rep(i,n) a[i]/=g;
	return 1-cal(a);
}
int main()
{
	vector<int> a;int n,x;
	cin>>n;
	rep(i,n){
		cin>>x;a.pb(x);
	}
	if(cal(a)==0) cout<<"First"<<endl;
	else cout<<"Second"<<endl;
}

