const int maxn = 1e5 + 69, lg = 19;

bool solve(vector<int> &A){
    int O, E;
    for(auto a : A)
        if(a & 1)
            O++;
        else
            E++;
    if(E & 1)
        return 1;
    if(O > 1)
        return 0;
    for(int i= 0; i < A.size(); i++)
        if(A[i] & 1)
            if(A[i] == 1)
                return 0;
            else
                A[i] = (A[i] - 1) >> 1;
        else
            A[i] >>= 1;
    return !solve(A);
}


