	return v ? gcd(u % v, v) : u;
}

bool solve() {
	all = 0;
	vec.clear();
	for (int i = 0; i < n; i++) {
		all += arr[i];
		if (arr[i] & 1)
			vec.push_back(i);
	}
	if ((all & 1) != (n & 1))
		return true;
	if (vec.size() != 1 || arr[vec[0]] == 1)
		return false;
	arr[vec[0]]--;
	int GCD = arr[0];
	for (int i = 1; i < n; i++)
		GCD = gcd(GCD, arr[i]);
	for (int i = 0; i < n; i++) 
		arr[i] /= GCD;
	return !solve();
}

