bool win() {
  vector <int> pref(n + 1), suf(n + 1);
  pref[0] = 0;
  for (int i = 0; i < n; i++) {
    pref[i + 1] = __gcd(pref[i], a[i]);
  }
  suf[n] = 0;
  for (int i = n - 1; i >= 0; i--) {
    suf[i] = __gcd(suf[i + 1], a[i]);
  }
  int safe = 0;
  for (int i = 0; i < n; i++) {
    if (a[i] == 1) {
      continue;
    }
    int g = __gcd(pref[i], suf[i + 1]);
    if (g > 1 && (a[i] - 1) % g == 0) {
      a[i]--;
      for (int j = 0; j < n; j++) {
        a[j] /= g;
      }
      if (!win()) {
        return true;
      }
      for (int j = 0; j < n; j++) {
