      }
      a[i]++;
    } else {
      safe++;
    }
  }
  if (safe > 0) {
    long long sum = 0;
    for (int i = 0; i < n; i++) {
      sum += a[i] - 1;
    }
    if (sum % 2 == 1) {
      return true;
    }
  }
  return false;
}

