bool winner(vector<int> v) {
    if(*max_element(ALL(v)) == 1) return 0;
    int odd = 0, even = 0;
    for(auto &&x : v)
        (x & 1 ? odd : even)++;
    if(even & 1) return 1;
    if(odd >= 2) return 0;
    int G = 0;
    for(auto &&x : v) {
        if(x == 1) return 0;
        if(x & 1) x--;
        G = gcd(G, x);
    }
    for(auto &&x : v)
        x /= G;
    return !winner(v);
}

