const int MAXN = 1e5 + 3;

LL arr[MAXN], n;

bool solve() {
  bool flag = false;
  LL sum = 0, cnt = 0, even = 0;
  for(int i = 0; i < n; i++) {
    if(arr[i] == 1) {
      flag = true;
    }
    cnt += arr[i] % 2;
    sum += arr[i] - 1;
  }
  even = n - cnt;
  if(flag) {
    return sum % 2 == 1;
  }
  if(cnt == 1 and n % 2 == 1) {
    LL g = 0;
    for(int i = 0; i < n; i++) {
      if(arr[i] % 2) arr[i]--;
      g = gcd(g, arr[i]);
    }
    for(int i = 0; i < n; i++) {
      arr[i] /= g;
    }
    return !solve();
  }
  if(even % 2 == 1) return true;
  return false;
}

