  bool ok;
  if(n == 1) {
    if(arr[0] % 2 == 0) ok = true;
    else ok = false;
  } else {
    ok = solve();
  }
  if(ok) {
    cout << "First" << endl;
  } else {
    cout << "Second" << endl;
  }
  return 0;
}

