void input() {
	cin >> n;
	for (int i = 0; i < n; i++) {
		cin >> a[i];
		vc.push_back(a[i]);
		s += a[i];
	}
	sort(vc.begin(), vc.end());
}

void build(int v, int s, int e) {
	if (e - s == 1) {
		seg[v] = a[s];
		return;
	}
	int mid = (s + e) / 2;
	build(v << 1, s, mid);
	build(v << 1 | 1, mid, e);
	seg[v] = __gcd(seg[v << 1], seg[v << 1 | 1]);
}

void get(int l, int r, int v, int s, int e) {
	if (r <= s || e <= l)
		return;
	if (l <= s && e <= r) {
		if (gc > 0)
			gc = __gcd(gc, seg[v]);
		else
			gc = seg[v];
		return;
	}
	int mid = (s + e) / 2;
	get(l, r, v << 1, s, mid);
	get(l, r, v << 1 | 1, mid, e);
}

bool check(int a, int b) {
	if (!b)
		return false;
	a--;
	b = __gcd(a, b);
	return ((s - 1) / b) & 1 && have(b);
}

bool have(int x) {
	int l = 0, r = vc.size(), mid = (l + r) / 2;
	while (r - l > 1) {
		(a[mid] <= x)? l = mid: r = mid;
		mid = (l + r) / 2;
	}
	return x == a[l];
}

void solve() {
	if ((s ^ n) & 1) {
		cout << "First" << endl;
		return;
	}
	if (n == 1) {
		cout << "Second" << endl;
		return;
	}
	build(1, 0, n);
	for (int i = 0; i < n; i++) {
		gc = 0;
		if (i)
			get(0, i, 1, 0, n);
		if (i != n - 1)
			get(i + 1, n, 1, 0, n);
		if (check(a[i], gc)) {
			cout << "First" << endl;
			return;
		}
	}
	cout << "Second" << endl;
}

