const int N = 550129;

int n, a, cnt[N], si, ans;

vector<pair<int, int> > g[N];

int root[N], sz[N], cn;

int Root(int x)
{
    if (x == root[x]) return x;
    return root[x] = Root(root[x]);
}

void Merge(int v, int u)
{
    if (cnt[v] == 0 || cnt[u] == 0) return;
    int x = Root(v), y = Root(u);
    if (x == y) return;
    ans = ans + (v + u) * ((root[u] == u ? cnt[u] : 1) + (root[v] == v ? cnt[v] : 1) - 1);
    cn += (root[u] == u ? cnt[u] : 1) + (root[v] == v ? cnt[v] : 1) - 1;
    if (sz[x] < sz[y])
    {
        root[x] = y;
        sz[y] += sz[x];
    }
    else
    {
        root[y] = x;
        sz[x] += sz[y];
    }
    return;
}

signed main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    for (int i = 0; i < N; i++) root[i] = i, sz[i] = 1;
    cin >> n;
    for (int i = 0; i < n; i++)
    {
        cin >> a;
        cnt[a]++;
        si += a;
    }
    cnt[0]++;
    int f = 0;
    for (int upmask = (1 << 18); upmask >= 0; upmask--)
    {
        for (int mask = upmask; ; mask = (mask - 1) & upmask)
        {
            int x = upmask - mask;
            Merge(mask, x);
            f++;
            if (mask == 0) break;
        }
    }
    if (cn != n) cout << cn << "\n";
    cout << ans - si;
}

