#include<bits/stdc++.h>
using namespace std;

#define X first
#define Y second

const int MAXN = 1e5 + 5;
int seg[MAXN << 2];
int a[MAXN];
long long s;
int n, gc;

void input();
void build(int, int, int);
void get(int, int, int, int, int);
bool check(int, int);
void solve();

int main() {
	ios:: sync_with_stdio(0), cin.tie(0), cout.tie(0);
	input();
	solve();
	return 0;
}

void input() {
	cin >> n;
	for (int i = 0; i < n; i++) {
		cin >> a[i];
		s += a[i];
	}
}

void build(int v, int s, int e) {
	if (e - s == 1) {
		seg[v] = a[s];
		return;
	}
	int mid = (s + e) / 2;
	build(v << 1, s, mid);
	build(v << 1 | 1, mid, e);
	seg[v] = __gcd(seg[v << 1], seg[v << 1 | 1]);
}

void get(int l, int r, int v, int s, int e) {
	if (r <= s || e <= l)
		return;
	if (l <= s && e <= r) {
		if (gc > 0)
			gc = __gcd(gc, seg[v]);
		else
			gc = seg[v];
		return;
	}
	int mid = (s + e) / 2;
	get(l, r, v << 1, s, mid);
	get(l, r, v << 1 | 1, mid, e);
}

bool check(int a, int b) {
	if (!b)
		return false;
	a--;
	b = __gcd(a, b);
	return ((s - 1) / b) & 1;
}

void solve() {
	if ((s ^ n) & 1) {
		cout << "First" << endl;
		return;
	}
	if (n == 1) {
		cout << "Second" << endl;
		return;
	}
	build(1, 0, n);
	for (int i = 0; i < n; i++) {
		gc = 0;
		if (i)
			get(0, i, 1, 0, n);
		if (i != n - 1)
			get(i + 1, n, 1, 0, n);
		if (check(a[i], gc)) {
			cout << "First" << endl;
			return;
		}
	}
	cout << "Second" << endl;
}

