int win(vector<int> V) {
	int num[2]={};
	int odd;

	FORR(r,V) {
		num[r%2]++;
		if(r%2==1) odd=r;
	}

	if(num[0]%2==1) return 1;
	if(num[1]>1 || odd==1) return 0;
	int g=0;
	FORR(r,V) {
		if(r==odd) r--;
		g=__gcd(g,r);
	}
	FORR(r,V) r/=g;
