int gcd (int a, int b)
{
	if (!b) return a;
	else return gcd (b, a % b);
}

int n;

bool f (vector <int> v)
{
	int odd = 0;
	for (int a : v)
		odd += (a & 1);

	if ((v.size () - odd) & 1) {
		return true;
	}
	else {
		if (odd > 1) return false;
		else {
			int g = v[0];
			for (int a : v)
				if ((a & 1) && a != 1) g = gcd (g, a - 1);
				else g = gcd (g, a);

			for (int& a : v)
				a /= g;

			if (g != 1) return !f (v);
			else return false;
		}
	}
}

int main ()
{
	cin >> n;
	vector <int> v (n);

	for (int i = 0; i < n; i++)
		scanf ("%d", &v[i]);

	if (f (v)) cout << "First";
	else cout << "Second";
}

