int f(int sign){
    for(int i = 1; i <= n; i ++)
        k += (a[i] % 2);

    if((n - k) & 1)
        return sign;

    if(k > 1)
        return sign ^ 1;

    else{
        for(int i = 1; i <= n; i ++){
            if(a[i] == 1)
                return sign ^ 1;
            if(a[i] & 1)
                a[i] --;
            if(g == 0)
