            } else {
                ans = i % 2 == 1;
                break;
            }
        }
    }
    cout << (ans ? "First" : "Second") << endl;
    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int MN = 101234;
int a[MN], b, n;

bool gd() {
	if (n % 2 == 0)
		return true;
	int res = 0;
	for (int i = 0; i < n; i++)
		res += a[i] % 2;
	if (res >= 2)
		return true;
	for (int i = 0; i < n; i++) {
		if (a[i] % 2) {
			if (a[i] == 1)
				return true;
			a[i]--;
		}
		int g = 0;
		for (int i = 0; i < n; i++)
			g = __gcd(g, a[i]);
		for (int i = 0; i < n; i++)
			a[i] = a[i] / g;
		assert(g != 1);
	}
	b = !b;
	return false;
}

int main() {
	cin >> n;
	for (int i = 0; i < n; i++) 
		cin >> a[i];
	while (!gd());
	long long sum;
	for (int i = 0; i < n; i++)
		sum += a[i];
	if (n == 1 && sum == 1)
		cout << "Second";
	else if (n == 1)
	 	cout << "First";
	else
		return cout << (b ^ (n % 2) ^ (sum % 2)? "First": "Second"), 0;
}

