void print_2() {
    cout << "Second\n";
}

int solve(int n) {
    ll sum = 0;
    ll odd_num = 0;
    for (int i = 0; i < n; i++) {
        sum += a[i];
        odd_num += (a[i] & 1);
    }
    if ((sum + n) % 2 == 0) {
        if (odd_num != 1)
            return 2;
        int u;
        for (int i = 0; i < n; i++) {
            if (a[i] & 1) {
                u = i;
                break;
            }
        }
        if (a[u] == 1)
            return 2;
        a[u]--;
        ll g = 0;
        for (int i = 0; i < n; i++) {
            g = __gcd(g, a[i]);
        }
        for (int i = 0; i < n; i++) {
            a[i] /= g;
        }
        return 3 - solve(n);
    }
    return 1;

}


