bool dp(int n,vector<ll> v){
  ll sum=0 ,cnt=0;
  rep(i,v.size()){
    sum+=v[i];
    sum%=2;
    if(v[i]&1)cnt++;
    }
  if(sum){
    if(!(n&1)) return true;
    if(cnt>2)return false;
    if(v[0]==1)return false;
    rep(i,n){
      if(v[i]&1)v[i]--;
      }
    ll g=v[0];
    rep(i,n-1)g=gcd(g,v[i+1]);
    rep(i,n)v[i]/=g;
    return !dp(n,v);
    }
  else{
    if(n%2)return true;
    return false;
    }
  }


