int N;
int A[MAX_N];

bool sub_solve() {
	if(N == 1) return (A[0] - 1) % 2;
	bool second = false;
	while(true) {
		int odd = 0;
		bool one = 0;
		rep(i, 0, N) {
			if(A[i] % 2) odd++;
			if(A[i] == 1) one = true;
		}
		if((N - odd) % 2) return (second ^ 1);
		else if(odd != 1 || one) return second;
		else {
			int m2 = inf;
			rep(i, 0, N) {
				if(A[i] % 2) A[i]--;
				int tmp = 1, n = A[i];
				while(n % 2 == 0) {
					n /= 2;
					tmp *= 2;
				}
				MIN(m2, tmp);
			}
			rep(i, 0, N) A[i] /= m2;
		}
		second ^= 1;
	}
}

void solve() {
	cin >> N;
	rep(i, 0, N) cin >> A[i];
	if(sub_solve()) {
		cout << "First\n";
	}
	else cout << "Second\n";
}

