}

bool Solve()
{
	if (n==1) return a[1]!=1;

	bool flag=true;
	for (int i=1;i<=n;i++) if (a[i]!=1) flag=false;
	if (!flag) return 0;

	int cnt=0;
	for (int i=1;i<=n;i++) if (!(a[i]&1)) cnt++;
	if (cnt&1) return 1;
	else 
	{
		if (cnt!=n-1) return 0;
		bool flag=false;
		for (int i=1;i<=n;i++) if (a[i]>1 && (a[i]&1))
		{
