void done(string s) {
	cout << s << endl;
	exit(0);
}

void prep() {
	odd = 0; even = 0;
	int gcd = -1;
	for (int i = 1; i <= n; i++) {
		if (gcd == -1) gcd = a[i];
		else gcd = __gcd(a[i], gcd);
	}
	for (int i = 1; i <= n; i++) {
		a[i] /= gcd;
		if (a[i] % 2 == 0) even++;
		else odd++;
	}
}

void process() {
	odd = 0; even = 0;
	int gcd = -1;
	for (int i = 1; i <= n; i++) {
		if (a[i] == 1) {
			if ((n - 1) % 2 == 1) done("First");
			else done("Second");
		}
		else if (a[i] % 2 == 1) a[i]--;
		if (gcd == -1) gcd = a[i];
		else gcd = __gcd(a[i], gcd);
	}
	for (int i = 1; i <= n; i++) {
		a[i] /= gcd;
		if (a[i] % 2 == 0) even++;
		else odd++;
	}
}

void check() {
	if (even == 0) done("First");
	if (even % 2) done("First");
	if (odd >= 2) done("Second");
}

