ll pw(ll a, ll b, ll mod) {
    if (!b)    return 1;
    if (b & 1) return a * pw(a * a % mod, b / 2, mod) % mod;
    return pw(a * a % mod, b / 2, mod) % mod;
}

const ll N    = 1e5 + 10;
const ll MOD  = 1e9 + 7;
const ll INF  = 1e18;

int n, A[N], even, odd, ind;

bool solve() {
    for (int i = 0; i < n; i++) {
        if (A[i] & 1) {
            odd++;
            ind = i;
        }
    }
    even = (n - odd);
    if (even & 1)
        return true;
    if (odd > 1)
        return false;
    A[ind]--;
    int g = 0;
    for (int i = 0; i < n; i++) {
        g = __gcd(g, A[i]);
    }
    for (int i = 0; i < n; i++) {
        A[i] /= g;
    }
    return !solve();
}

