		b = q;
	}
	return a;
}

int solve() {
	int c0 = 0;
	int c1 = 0;
	for (int i = 0; i < n; ++i) {
		if (a[i] % 2 == 0)
			++c0;
		else
			++c1;
	}
	if (c0 % 2 == 1)
		return 1;
	if (c0 % 2 == 0 && c1 >= 2)
		return 0;
	for (int i = 0; i < n; ++i) {
		if (a[i] % 2 == 1) {
			if (a[i] == 1)
				return 0;
			--a[i];
