void experiment(void) {
  REP(i, 1, n) {
    cerr << "dp[" << i << "]:" << endl;
    REP(j, 1, n) {
      cerr << "dp[" << i << "," << j << "]:";
      REP(k, 1, n) {
	ll res = 1;
	if (i + j + k == 3) {
	  dp[i][j][k] = 0;
	  continue;
	}
	int g = __gcd(i, j);
	g = __gcd(g, k);
	if (g != 1) {
	  res = 1 - dp[i / g][j / g][k / g];
	} else {
	  if (i >= 2) {
	    res &= dp[i - 1][j][k];
	  }
	  if (j >= 2) {
	    res &= dp[i][j - 1][k];
	  }
	  if (k >= 2) {
	    res &= dp[i][j][k - 1];
