bool winning(const VL &a) {
  int n = a.size();
  ll evenodd = 0;
  int numodd = 0;
  REP(i, 0, n) {
    evenodd += a[i] - 1;
    numodd += a[i] % 2 == 1 ? 1 : 0;
  }
  if (evenodd % 2 == 1) {
    return true;
  }
  assert (numodd >= 1);
  if (n <= 2 || numodd >= 2) {
    return false;
  }
  int p = -1;
  REP(i, 0, n) {
    if (a[i] % 2 == 1) {
      p = i;
      break;
    }
  }
  if (a[p] == 1) {
    return false;
  }
  VL b(n);
  REP(i, 0, n) {
    b[i] = a[i] - (i == p ? 1 : 0);
  }
  ll g = b[0];
  REP(i, 1, n) {
    g = __gcd(g, b[i]);
  }
  REP(i, 0, n) {
    b[i] /= g;
  }
  return not winning(b);
}

