		if (c[1] > 1 || c[0] + c[1] == 1) break;
		bool fail = false;
		iter(i, n) if (a[i] % 2) {
			if (a[i] > 1) --a[i];
			else { fail = true; break; }
		}
		if (fail) break;
		int d = a[1];
		iter(i, n) d = gcd(d, a[i]);
		iter(i, n) a[i] /= d;
		cur ^= 1;
	}

	puts(cur ? "First" : "Second");
	return 0;
}

