int gcd(int a, int b) {
	if (a % b == 0) {
		return b;
	} else {
		return gcd(b, a % b);
	}
}

int calc(vector <int> &v) {
	int c = -1, g = 0, i;
	long long sum = 0;

	sort(v.begin(), v.end());

	for (i = 0; i < v.size(); i++) sum += v[i] - 1;

	if (v[0] == 1) return sum % 2;

	for (i = 0; i < v.size(); i++) {
		if (v[i] % 2 == 1) {
			if (c != -1) return sum % 2;

			c = i;
		}
	}

	v[c]--;

	for (i = 0; i < v.size(); i++) g = gcd(g, v[i]);

	for (i = 0; i < v.size(); i++) v[i] /= g;

	return calc(v);
}

