int n;
int a[MAXN];

bool check()
{
	int cnt = 0;
	for (int i = 1;i <= n;i++)
		cnt += (a[i] & 1);
	int step = ((n - cnt) & 1);
	if (cnt > 1 && !step)
		return 1;
	if (cnt >= 1 && step)
		return 0;
	for (int i = 1;i <= n;i++)
		if (a[i] & 1)
			a[i]--;
	for (int i = 1;i <= n;i++)
		a[i] >>= 1;
	return check() ^ 1;
}

