{
	scanf("%d",&n);
	for (int i = 1;i <= n;i++)
		scanf("%d",&a[i]);
	if (n == 1)
	{
		puts((a[1] & 1) ? "Second" : "First");
		return 0;
	}
	puts(check() ? "Second" : "First");
    return 0;
}

