	return (y == 0)? x : gcd(y, x % y);
}

void play(int player) {
	int even = 0;
	for (int i=1;i<=n;++i) {
		even += !(a[i] & 1);
	}
	if (even & 1) output(1 ^ player);
	if (n - even > 1) output(0 ^ player);
	int d = 0;
	for (int i=1;i<=n;++i) {
		if (a[i] == 1) output(0 ^ player);
		d = gcd(d, a[i] - (a[i] & 1));
	}
	for (int i=1;i<=n;++i) {
		a[i] /= d;
	}
	play(player ^ 1);
}

void solve() {
	read(n);
	for (int i=1;i<=n;++i) {
		read(a[i]);
	}
	play(0);
}

