int n, a[100000];

bool solve() {
	LL sum = 0;
	inc(i, n) { sum += a[i]; }

	if(sum == n) { return false; }
	if((sum - n) % 2 == 1) { return true; }
	if(n % 2 == 0) { return false; }

	LL c = 1, ms = 0;
	while((sum - 1) % (c * 2) == 0) { c *= 2; }
	inc(i, n) {
		if(a[i] < c) { return false; }
		ms += a[i] % c;
		a[i] /= c;
	}
	if(ms == 1) { return ! solve(); } else { return false; }
}

