int dp[1<<18];

void update(int idx, int num) {
  dp[idx] = num;
  while (idx > 1) {
    idx /= 2;
    dp[idx] = __gcd(dp[idx*2], dp[idx*2+1]);
  }
}

map<int, int> ret;

bool estimate(vector<int>& A, int mult) {
  if (ret.find(mult) != ret.end()) return ret[mult];
  int even = 0;
  for (int i=0; i<A.size(); i++)
    if ((A[i]/mult)%2 == 0) even ++;
  if (even % 2 == 1) return true;

  bool res = false;
  for (int i=0; i<A.size(); i++) {
    if (A[i] == mult) continue;
    A[i] -= mult;
    update(i + (1<<17), A[i]);
//    printf("%d\n", dp[1]);
    if (dp[1] > mult && !estimate(A, dp[1])) res = true;
    A[i] += mult;
    update(i + (1<<17), A[i]);
  }
//  for (int i=0; i<A.size(); i++) printf("%d ", A[i]);
//  printf("%d = %d\n", mult, res);
  return ret[mult] = res;
}

int main()
{
  int N;
  cin >> N;
  vector<int> A(N);
  for (int i=0; i<N; i++) {
    cin >> A[i];
    update((1<<17)+i, A[i]);
  }
  if (estimate(A, 1)) printf("First\n"); else printf("Second\n");
}

