ll gcd(ll a, ll b){
	if (a<b)swap(a,b);
	if (b == 0)return a;
	return gcd(b, a%b);
}

ll query(){
	// for (ll i=1;i<=N;++i)cout<<A[i]<<' ';cout<<'\n';
	a = 0;
	for (ll i=1;i<=N;++i)a += A[i]-1;
	if (a%2 == 1){
		return 1;
	}
	// Other case is if everything except 1 thing is even
	ll ind = -1;
	for (ll i=1;i<=N;++i){
		if (A[i] == 1)return 2;
		if (A[i]%2 == 1){
			if (ind == -1)ind = i;
			else {
				return 2;
			}
		}
	}
	if (ind == -1){return 2;}
	--A[ind];
	ll l = A[1];
	for (ll i=2;i<=N;++i)l = gcd(l, A[i]);
	assert(l != 1);

	for (ll i=1;i<=N;++i)A[i] /= l;
	return 3 - query();
}

