void solve(vector<lli> a, lli depth)
{
    bool flag = false;
    int odd = 0, even = 0;
    rep(i, n)
    {
        if (a[i] == 1) {
            flag = true;
        }
        if (a[i] & 1) {
            odd++;
        } else {
            even++;
        }
